INSERT INTO `email_templates` (`id`, `email_type`, `email_subject`, `email_body`, `status`) VALUES
(1, 'new_order', 'Your Order Placed Successfully', '<p>Hello {customer_name},<br>Your Order Number is {order_number}<br>Your order has been placed successfully</p>', 1),
(2, 'new_registration', 'Welcome To Kahioja', '<p>Hello {customer_name},<br>You have successfully registered to {website_title}, We wish you will have a wonderful experience using our service.</p><p>Thank You<br></p>', 1),
(3, 'vendor_accept', 'Vendor Account Activated', '<p>Hello {customer_name},<br>Congratulations, your vendor account is activated.&nbsp;</p><p><br></p><p>Login to your account and let\'s get started! [link]</p><p><br></p><p>Thank You<br></p>', 1),
(4, 'subscription_warning', 'Renew your subscription', '<p>Hello {customer_name},</p><p><br></p><p>Your subscription at <b>KAHIOJA</b> is about to expire on [date]. We hope you’ve enjoyed [benefits of your membership].&nbsp;</p><p><br></p><p>Good news! There’s still time to renew, and it’s as easy as ever – just click the link below, pick the subscription that suits your needs and follow the prompts.</p><p><br></p><p>RENEW NOW</p><p><br></p><p><i><font face=\"times new roman\">If you have any questions regarding your subscription, benefits, or renewal, please don’t hesitate to reach out by replying to this email or calling us at [number].</font></i></p><p><br></p><p>Thank you.</p><p><br></p>', 1),
(5, 'vendor_verification', 'Email Verification.', '<p>Hello {customer_name},</p><p><br></p><p>Thanks for getting started with <b>KAHIOJA</b>!</p><p><br></p><p>We need a little more information to complete your registration, including a confirmation of your email address and a copy of your government issued ID.</p><p><br></p><p>Click below to confirm your email address:</p><p><br></p><p>[link]</p><p><br></p><p><i><font face=\"times new roman\">If you have problems, please paste the above URL into your web browser.&nbsp;</font></i></p><p><br></p><p>Thank You<br></p><p><br></p>', 1);
INSERT INTO `faqs` (`id`, `title`, `details`, `status`) VALUES
(1, 'What is the estimated delivery time?', '<div class=\"a0a23_garq5\" id=\"section-5\" style=\"padding: 1.875rem 0px; color: rgb(28, 29, 31); font-family: &quot;Helvetica Neue&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Oxygen, Ubuntu, Cantarell, &quot;Fira Sans&quot;, &quot;Droid Sans&quot;, sans-serif; font-size: 13px;\"><div class=\"beca8_1ud3N\" style=\"color: rgb(143, 143, 143); font-size: 0.9375rem; line-height: 1.5; width: 755.281px;\"><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px;\">Kahioja delivers products to customers all over Nigeria. Lagos 2-5, Rivers 3-7, Abuja 3-8 &amp; other states 4-9 days</p></div></div><div class=\"a0a23_garq5\" id=\"section-6\" style=\"padding: 1.875rem 0px; color: rgb(28, 29, 31); font-family: &quot;Helvetica Neue&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Oxygen, Ubuntu, Cantarell, &quot;Fira Sans&quot;, &quot;Droid Sans&quot;, sans-serif; font-size: 13px;\"></div>', 1),
(3, 'What is buyer protection?', '<div class=\"a0a23_garq5\" id=\"section-4\" style=\"padding: 1.875rem 0px; color: rgb(28, 29, 31); font-family: &quot;Helvetica Neue&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Oxygen, Ubuntu, Cantarell, &quot;Fira Sans&quot;, &quot;Droid Sans&quot;, sans-serif; font-size: 13px;\"><div class=\"beca8_1ud3N\" style=\"color: rgb(143, 143, 143); font-size: 0.9375rem; line-height: 1.5; width: 755.281px;\"><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px;\">We offer all our customers ultimate peace of mind. We have you covered 100% on every eligible purchase. Visit the&nbsp;<a href=\"https://konga.com/help/buyer-safety\" target=\"_blank\" rel=\"noopener\" style=\"color: rgb(154, 0, 82); cursor: pointer; text-decoration: inherit;\"><strong>Buyer Safety Page</strong></a>&nbsp;to learn more.</p></div></div><div class=\"a0a23_garq5\" id=\"section-5\" style=\"padding: 1.875rem 0px; color: rgb(28, 29, 31); font-family: &quot;Helvetica Neue&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Oxygen, Ubuntu, Cantarell, &quot;Fira Sans&quot;, &quot;Droid Sans&quot;, sans-serif; font-size: 13px;\"></div>', 1),
(4, 'Why do I see different prices for the same product?', '<div class=\"a0a23_garq5\" id=\"section-3\" helvetica=\"\" neue\",=\"\" -apple-system,=\"\" blinkmacsystemfont,=\"\" \"segoe=\"\" ui\",=\"\" roboto,=\"\" oxygen,=\"\" ubuntu,=\"\" cantarell,=\"\" \"fira=\"\" sans\",=\"\" \"droid=\"\" sans-serif;=\"\" font-size:=\"\" 13px;\"=\"\" style=\"padding: 1.875rem 0px; color: rgb(28, 29, 31);\"><div class=\"beca8_1ud3N\" style=\"color: rgb(143, 143, 143); font-size: 0.9375rem; line-height: 1.5; width: 755.281px;\"><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px;\">Kahioja is your trusted online marketplace that supports Nigerian entrepreneurs and we have many different sellers competing for business. Therefore, you may see the same product offered by different sellers at different prices. We believe that by supporting these \'third-party sellers, we can offer you a wider product selection, more choice, increased convenience, and better pricing.</p><div><br></div></div></div>', 0),
(5, 'Will all of the items in my order arrive in a single package?', '<div class=\"a0a23_garq5\" id=\"section-2\" style=\"padding: 1.875rem 0px; color: rgb(28, 29, 31); font-family: &quot;Helvetica Neue&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Oxygen, Ubuntu, Cantarell, &quot;Fira Sans&quot;, &quot;Droid Sans&quot;, sans-serif; font-size: 13px;\"><div class=\"beca8_1ud3N\" style=\"color: rgb(143, 143, 143); font-size: 0.9375rem; line-height: 1.5; width: 755.281px;\"><p style=\"margin-right: 0px; margin-left: 0px; padding: 0px;\">If your order consists of items from more than one seller, your items will arrive separately. If your order consists of items from a single seller, your items may arrive together or separately. If your orders arrive separately, be rest assured that the remaining item(s) will be delivered shortly.</p></div></div><div class=\"a0a23_garq5\" id=\"section-3\" style=\"padding: 1.875rem 0px; color: rgb(28, 29, 31); font-family: &quot;Helvetica Neue&quot;, -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, Oxygen, Ubuntu, Cantarell, &quot;Fira Sans&quot;, &quot;Droid Sans&quot;, sans-serif; font-size: 13px;\"></div>', 0),
(6, 'Are your Products available for wholesale or Distribution?', '<font color=\"#8f8f8f\">It all depends on the product in demand, availability and the vendor. Please contact us to inquire&nbsp;about distribution and wholesale opportunity.&nbsp;&nbsp;</font>', 0),
(10, 'Who will deliver my order?', '<div>Kahioja is affiliated with reliable third party logistics that help us serve our customers better.&nbsp;<br></div><div><br></div><div><div>Orders being handled within our fulfilment centers will be handled and delivered by Kahioja.</div></div>', 0),
(12, 'How do I sell on Kahioja?', 'Selling your product on Kahioja is pretty easy. All you need to do is <a href=\"https://kahioja.com/\" title=\"\" target=\"_blank\">click</a>&nbsp;here, go to the top right corner of the page and register as a vendor.', 0);
